import React, { useEffect, useState } from 'react';
import axios from 'axios';
import XrpWallet from './XrpWallet';
// eslint-disable-next-line
import something from '@scure/base';
import LandingPage from './LandingPage';
import Account from './Account'; // Corrected import path - assuming 'account' is the name of the component file

function App() {
  const [walletInfo, setWalletInfo] = useState(null);

  useEffect(() => {
    axios.get('/account')
      .then(response => {
        setWalletInfo(response.data);
      })
      .catch(error => {
        console.error('Error fetching account:', error);
      });
  }, []);

  return (
    <div>
      <h1>XRP Wallet Information</h1>
      {walletInfo && (
        <div>
          <p>Wallet Address: {walletInfo.wallet.address}</p>
          <p>Secret Key: {walletInfo.wallet.secret}</p>
          <p>Fund Result: {JSON.stringify(walletInfo.fundResult)}</p>
        </div>
      )}
      <XrpWallet /> {/* Render the XrpWallet component */}
      <div>
        <Account /> {/* Render the account component */}
      </div>
      <div>
      </div>
    </div>
  );
}

export default App;
