import React from "react";
import { Link } from "react-router-dom";
import bannerImg from "./img/fin.jpg";
import "./profile.css"; // Import profile.css instead of AboutUs.css
import Calculator from "./Calculator";
import Container from "react-bootstrap/Container";
import logoftp from "./img/fin.jpg";
import avatar from "./img/fin.jpg";
import abdur from "./img/fin.jpg";
import Thani from "./img/fin.jpg";

const About = () => {

    const [walletInfo, setWalletInfo] = useState(null);

  useEffect(() => {
    // Fetch account information when the component mounts
    axios.get('/account')
      .then(response => {
        setWalletInfo(response.data);
      })
      .catch(error => {
        console.error('Error fetching account:', error);
      });
  }, []);
  return (
    <div className="row text-center">
      <div className="aboutUs">
        <div className="grid-container-aboutUs">
          <div className="grid-item-aboutUs">
            <hr />
          </div>

          <div className="grid-item-aboutUs">
            <h2>Our Mission</h2>
            <p>
              Our mission is to make the world more sustainable while having
              the farms and local shops connected with the local community. Our
              aim to have all the farms of London registered with us within
              July 2023 and cover the whole UK within December 2024. We plan to
              play a prominent role in the world of fresh produces from year
              2025.
            </p>
            <hr />
          </div>
          <div className="grid-item-aboutUs">
            <hr />
          </div>
        </div>
        <h2>Your Profile</h2>
        <div className="grid-container">
          <div className="grid-item"> {/* Updated class name */}
            <img className="banner-img" src={avatar} alt="Bannerimg" />
            <h4 className="font-weight-bold dark-grey-text mt-4">Carlotta</h4>
            <h6 className="font-weight-bold blue-text my-3">
              Scrum Master: Sprint1A
            </h6>
            <p className="font-weight-normal dark-grey-text"></p>
          </div>
          <Calculator/>
        </div>
          {/* Display XRP wallet information if available */}
      <div className="container">
        <h1>XRP Wallet Information</h1>
        {walletInfo && (
          <div>
            <p>Wallet Address: {walletInfo.wallet.address}</p>
            <p>Secret Key: {walletInfo.wallet.secret}</p>
            <p>Fund Result: {JSON.stringify(walletInfo.fundResult)}</p>
          </div>
        )}
      </div>
      </div>
    </div>
  );
};

export default About;
