// Header.js
import React, { useState } from "react";
import logo from "./img/logo.png";
import { AiOutlineMenu, AiOutlineMinus } from "react-icons/ai";
import { Link } from "react-router-dom";
import { useNavigation } from "./NavigationContext";

const Header = () => {
  const { navigate } = useNavigation();
  const [click, setClick] = useState(false);
  const handleClick = () => setClick(!click);

  return (
    <header className="header1">
      <div>
        <img src={logo} alt="Logo" className="logo" />
      </div>

      <ul className={click ? "nav-menu1 active1" : "nav-menu1"}>
        <li>
          <Link to="/" className="wallet-button" onClick={() => navigate("/")}>Home</Link>
        </li>
        <li>
          <Link to="/our-services" className="wallet-button" onClick={() => navigate("/our-services")}>Our Services</Link>
        </li>
        <li>
          <Link to="/profile" className="wallet-button" onClick={() => navigate("/profile")}>Profile</Link>
        </li>
        <li>
          <Link to="/login" className="wallet-button" onClick={() => navigate("/login")}>Log In</Link>
        </li>
      </ul>

      <div className="menubutton1" onClick={handleClick}>
        {click ? (
          <AiOutlineMinus size={25} style={{ color: "#fff" }} />
        ) : (
          <AiOutlineMenu size={25} style={{ color: "#fff" }} />
        )}
      </div>
    </header>
  );
};

export default Header;
