import React from 'react';
import Web3 from 'web3';

const MetaInfo = async () => {
  try {
    // Check if MetaMask is installed
    if (window.ethereum) {
      const web3 = new Web3(window.ethereum);

      // Request access to the user's MetaMask accounts
      const accounts = await window.ethereum.request({ method: 'eth_requestAccounts' });

      // Get the balance of the user's account
      const balance = await web3.eth.getBalance(accounts[0]);

      // Convert the balance from Wei to Ether
      const balanceInEther = web3.utils.fromWei(balance, 'ether');
      console.log('Account balance in Ether:', balanceInEther);

    } else {
      console.error('MetaMask not found. Please install MetaMask to use this feature.');
    }
  } catch (error) {
    console.error('Error reading data:', error);
  }

  // Render logic can be added here if needed
  return (
    <div>
      {/* Add any JSX content here if needed */}
    </div>
  );
};

export default MetaInfo;
