import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Header from './Header';
import Footer from './Footer';
import LandingPage from './LandingPage';
import Intro from './Intro';
import Calculator from './Calculator';
import Team from './Team';
import XrpWallet from './XrpWallet';
import Account from './Account';
import Profile from './Profile';
import LogIn from './LogIn';
function App() {
  const [walletInfo, setWalletInfo] = useState(null);

  useEffect(() => {
    // Fetch account information when the component mounts
    axios.get('/account')
      .then(response => {
        setWalletInfo(response.data);
      })
      .catch(error => {
        console.error('Error fetching account:', error);
      });
  }, []);

  return (
    <div>
      <Header />


      {/* Display XRP wallet information if available */}
      <div className="container">
        <h1>XRP Wallet Information</h1>
        {walletInfo && (
          <div>
            <p>Wallet Address: {walletInfo.wallet.address}</p>
            <p>Secret Key: {walletInfo.wallet.secret}</p>
            <p>Fund Result: {JSON.stringify(walletInfo.fundResult)}</p>
          </div>
        )}
      </div>

      {/* Main sections of the application */}
      <LandingPage />
      <Intro />
      <Calculator />
      <Team />
      <XrpWallet />
      <LogIn/>
      <Footer />
    </div>
  );
}

export default App;
