import React, { useEffect, useState } from 'react';
import { Client, Wallet, dropsToXrp, rippleTimeToISOTime } from 'xrpl';

const XrpWallet = () => {
    const [accountInfo, setAccountInfo] = useState(null);
    const [ledgerDetails, setLedgerDetails] = useState(null);

    useEffect(() => {
        const client = new Client(process.env.CLIENT); // Use the environment variable for client URI

        const fetchAccountInfo = async () => {
            try {
                await client.connect();

                const wallet = Wallet.fromSeed(process.env.REACT_APP_SEED); // Make sure your .env variable is prefixed with REACT_APP_
                const { account_data } = await client.request({
                    command: 'account_info',
                    account: wallet.address,
                    ledger_index: 'validated',
                });

                setAccountInfo(account_data);

                await client.request({
                    command: 'subscribe',
                    streams: ['ledger'],
                });

                client.on('ledgerClosed', (ledger) => {
                    setLedgerDetails(ledger);
                });
            } catch (error) {
                console.error('Error creating wallet:', error); // Log error message
                console.error('Error connecting to the XRP Ledger:', error); // Keep existing error logging
            }
        };

        fetchAccountInfo();

        return () => {
            client.disconnect();
        };
    }, []);

    return (
        <div>
            <h2>Account Information:</h2>
            {accountInfo && (
                <div>
                    <p><strong>Account Address:</strong> {accountInfo.Account}</p>
                    <p><strong>Balance:</strong> {dropsToXrp(accountInfo.Balance)} XRP</p>
                </div>
            )}
            <h2>Latest Ledger Details:</h2>
            {ledgerDetails && (
                <div>
                    <p><strong>Ledger Index:</strong> {ledgerDetails.ledger_index}</p>
                    <p><strong>Ledger Hash:</strong> {ledgerDetails.ledger_hash}</p>
                    <p><strong>Close Time:</strong> {rippleTimeToISOTime(ledgerDetails.ledger_time)}</p>
                </div>
            )}
        </div>
    );
};

export default XrpWallet;
