import React, { useState } from 'react';
import axios from 'axios';

const Calculator = () => {
  const [amount, setAmount] = useState('');
  const [coins, setCoins] = useState(null);
  const [error, setError] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('/calculate-coins', { amount });
      setCoins(response.data.coins);
      setError(null);
    } catch (error) {
      console.error('Error calculating coins:', error.message);
      setError('Error calculating coins');
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <label>
          Enter Amount:
          <input
            type="number"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
          />
        </label>
        <button type="submit">Calculate Coins</button>
      </form>
      {coins !== null && <p>Coins: {coins}</p>}
      {error && <p>{error}</p>}
    </div>
  );
};

export default Calculator;
