import React, { useState } from 'react';
import axios from 'axios';

const CoinCalculator = () => {
  const [amount, setAmount] = useState('');
  const [coins, setCoins] = useState(null);
  const [error, setError] = useState(null);

  const handleChange = (event) => {
    setAmount(event.target.value);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await axios.post('/calculate-coins', { amount });
      setCoins(response.data.coins);
      setError(null);
    } catch (error) {
      console.error('Error calculating coins:', error);
      setError('Error calculating coins');
      setCoins(null);
    }
  };

  return (
    <div>
      <h2>Coin Calculator</h2>
      <form onSubmit={handleSubmit}>
        <label htmlFor="amount">Enter amount of money: </label>
        <input
          type="number"
          id="amount"
          value={amount}
          onChange={handleChange}
          required
        />
        <button type="submit">Calculate Coins</button>
      </form>
      {coins && <p>Number of coins: {coins}</p>}
      {error && <p style={{ color: 'red' }}>{error}</p>}
    </div>
  );
};

export default CoinCalculator;
