import React from "react";
import "./profile.css"; // Import profile.css instead of AboutUs.css
import avatar from "./img/fin.jpg";
import Calculator from "./Calculator";
import LandingPage from "./LandingPage";
import XrpWallet from "./XrpWallet";

const Profile = () => {
  const handleWalletClick = () => {
    // Handle the click event to open the XrpWallet component
    // For example, you can navigate to the XrpWallet component or open a modal
  };

  return (
    <div className="container">
      <div className="aboutUs">
        <div className="welcome">
          <h2>Welcome Back, Alex!</h2>
          <p>You are on the right side, Alex! See what we got for you!</p>
        </div>
        <h2>Your Profile</h2>
        <div className="grid-container">
          <div className="profile">
            {/* Updated class name */}
            <img className="avatar" src={avatar} alt="Avatar" />
            <h4 className="name">Alex</h4><br/>
            <h6 className="status">VIP Customer: 1300 XRPs!</h6>
          </div>
          <div className="calculator">
            <Calculator />
          </div>
          <div className="landing">
            <XrpWallet/>
          </div>
        </div>
        <button onClick={handleWalletClick}>Your Wallet</button> {/* Add the button here */}
      </div>
    </div>
  );
};

export default Profile;
