import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Header from './Header';
import Footer from './Footer';
import XrpWallet from './XrpWallet';
import Account from './Account'; // Corrected import path

const LandingPage = () => {
  const [walletInfo, setWalletInfo] = useState(null);

  useEffect(() => {
    const fetchAccountInfo = async () => {
      try {
        const response = await axios.get('http://localhost:5001/account'); // Update the URL to match your backend server
        setWalletInfo(response.data);
      } catch (error) {
        console.error('Error fetching account:', error);
      }
    };

    fetchAccountInfo();
  }, []);

  return (
    <>
      <Header />
      <div>
        <h1>XRP Wallet Information</h1>
        {walletInfo && (
          <div>
            <p>Wallet Address: {walletInfo.wallet.classicAddress}</p>
            <p>Secret Key: {walletInfo.wallet.seed}</p>
            <p>Fund Result: {JSON.stringify(walletInfo.fundResult)}</p>
          </div>
        )}
        <XrpWallet />
        <div>
          {/* Other content goes here */}
        </div>
      </div>
      <Footer />
    </>
  );
}

export default LandingPage;
